"use strict";

var util = require('util');
var Agent = require('orion-core/lib/model/test/Agent');

/**
 * Embedded browser's agent
 */
class EmbeddedAgent extends Agent {


    ctor() {
        var me = this;
        me.browser.on('terminated', () => {
            me.fire({
                type: 'terminated',
                agent: me
            });
        });
    }
    
}

module.exports = EmbeddedAgent;
