ST.Url = ST.define({
    constructor: function (url) {
        var parser = document.createElement('a');
            parser.href = url;
        
        var key, i,
            urlObj = {
                // capture original requested url, in case it's a partial
                originalUrl: url
            }, 
            props = [
                'hash',
                'host',
                'hostname',
                'href',
                'password',
                'port',
                'protocol',
                'search',
                'username'
            ];

        for (i=0; i<props.length; i++) {
            key = props[i];
            urlObj[key] = parser[key];
        }
        
        ST.apply(this, urlObj);

        return this;
    },

    getHash: function () {
        return this.hash;
    },

    isOnlyHash: function () {
        var originalUrl = this.getOriginal();

        return originalUrl === '#' || originalUrl === this.getHash();
    },

    get: function () {
        return this.href;
    },

    getOriginal: function () {
        return this.originalUrl;
    }
});
