'use strict';

var Image = require('orion-core/lib/image/Image');
var xfs = require('orion-core/lib/xfs');

require('jasmine-co').install();

describe('Image', function () {
    
    it('loads png files', function (done) {
        var image = new Image(xfs.resolve('test/image/actual.png'));
        image.load().then(done);
    });
    
    it('loads big png files', function (done) {
        var image = new Image(xfs.resolve('test/image/bigone.png'));
        image.load().then(done);
    });
    
});
