'use strict';

/**
 * Thrown at startup if sencha cmd cannot be found.
 */
class SenchaCmdNotFoundError extends Error {

    /**
     * @param {String} required The minimum required sencha cmd version
     */
    constructor(required) {
        super('Sencha Cmd could not be found.');
        this.required = required;
    }
}

module.exports = SenchaCmdNotFoundError;