'use strict';

/**
 * Thrown at startup if the minimum required version of sencha cmd is not installed
 */
class SenchaCmdVersionError extends Error {

    /**
     * @param {String} required The minimum required version
     * @param {String} installed The version currently installed
     */
    constructor(required, installed) {
        super(`Sencha Cmd version ${installed} detected. Sencha Themer requires Sencha Cmd ${required} or newer.`);
        this.required = required;
        this.installed = installed;
    }

}

module.exports = SenchaCmdVersionError;