var Index = require('./Index');

module.exports = {
    /**
     * alias -> class name
     */
    alias: new Index('alias', { persistent: false }),

    /**
     * file path -> last modified time when indexed
     */
    file: new Index('file'),

    /**
     * Class name -> location
     */
    usage: new Index('usage'),

    /**
     * class name -> override metadata
     */
    override: new Index('override', { persistent: false }),

    /**
     * Holds store ids
     */
    store: new Index('store'),

    /**
     * Class name -> extJSClass
     */
    className: new Index('class', { persistent: false })
};