/**
 * @class Ext
 * @singleton
 */
Ext = {};

/**
 * @class Ext.ClassManager
 */
Ext.ClassManager = function() {
    Ext.apply(Ext, {
        /**
         * @member Ext
         * @param {String} name
         * @param {Object} config
         * @return {Object}
         */
        create: function() {},

        /**
         * @member Ext
         * @param {String} name
         * @param {Object} config
         * @return {Ext.Class}
         */
        widget: function() {},

        /**
         * @member Ext
         * @param {String} name
         * @param {Object} config
         * @return {Ext.Class}
         */
        define: function() {},

        /**
         * @member Ext
         * @param {String} name
         * @param {Object} config
         * @return {Ext.Class}
         */
        createByAlias: function() {}
    });
};

